function truncateString(str, num) {
  var w = str.split("");
  let newArr = [];
  if (num < w.length) {
    for (var i = 0; i < num; i++) {
      newArr.push(w[i]);
    }
  var final = newArr.join("") + "...";
  return final;
  } else { return str }
}

